const chalk=require("chalk"),figlet=require("figlet");class Logger{constructor(o){this.name=o}static bigLog(o){console.log(chalk.yellow(figlet.textSync(o,{horizontalLayout:"full"})))}static log(o){console.log(o)}static success(o){console.log(chalk.green(`✓ ${o}`))}static warning(o){console.log(chalk.yellow(`! ${o}`))}static error(o,{verbose:l}={verbose:!1}){l?console.log(o):console.log(chalk.red(`✘ ${o}`))}}module.exports=Logger;